    def test_create_confusion_matrix_manual( self ):

        # declare variables
        confusion_helper = None
        metrics_dict = None
        calc_type = None
        error_message = None
        
        # do work
        calc_type = ConfusionMatrixHelper.CALC_TYPE_MANUAL
        confusion_helper = ConfusionMatrixHelper.populate_confusion_matrix( self.ACTUAL_VALUE_LIST,
                                                                            self.PREDICTED_VALUE_LIST,
                                                                            calc_type_IN = calc_type,
                                                                            derive_metrics_IN = True )
        
        # get metrics dict
        metrics_dict = confusion_helper.get_metrics_dict()
        
        # and the assert
        error_message = calc_type + " confusion matrix not equal to reference confusion matrix."
        self.assertDictEqual( metrics_dict, self.EXPECTED_OUTPUT_MAP, msg = error_message )
        
    #-- END method test_create_confusion_matrix_manual() --#


